INSERT INTO templates_acciones VALUES (151, 'contenedor_listado_solicitudes_rectificadas', '<table cellspacing="0" cellpadding="0" width="98%" border="0">\r\n    <tbody>\r\n        <tr>\r\n          <td valign="top"> </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign="top">\r\n                      \r\n            <table width="100%" border="0" cellpadding="0" cellspacing="0">\r\n              <tr>\r\n                <td><div align="left">\r\n                  <h3>Rectificar Solicitudes  </h3></div></td>\r\n              </tr>\r\n              <tr>\r\n                <td> </td>\r\n              </tr>\r\n              <tr>\r\n                <td><p align="justify">Las siguientes solicitudes se encuentran pendientes de rectificaci&oacute;n por parte del solicitante. Aquellas que ingresaron por sitios electronicos s&oacute;lo pueden ser modificadas por el solicitante y se puede acceder a cada una de ellas en el enlace "VER DETALLE". Para rectificar una solicitud ingresada en forma manual (carta  o formulario), acceda al enlace "RECTIFICAR".</p></td>\r\n              </tr>\r\n              <tr>\r\n                <td> &nbsp;</td>\r\n              </tr>\r\n              <tr>\r\n                <td><div align="left">Buscar por N&ordm; de solicitud:\r\n                    <input id="buscar" name="buscar" type="text" class="combo" />\r\n                  <input id="buscar2" type="submit" name="buscar2" value="Buscar..." class="boton" />\r\n                </div></td>\r\n              </tr>\r\n              <tr>\r\n                <td><strong>#FILTRO_ENTIDADES#Tipo</strong>:#TIPO#     <strong>Responsable:</strong>#RESPONSABLE#     <a href="#" onclick="document.form1.ms.value=1;document.form1.submit();"></a></td>\r\n              </tr>\r\n              <tr>\r\n                <td><a href="#" onclick="document.form1.ms.value=1;document.form1.submit();">Mis Solicitudes (#TOT_MIS_SOLICITUDES#)</a></td>\r\n              </tr>\r\n              <tr>\r\n               