INSERT INTO templates_acciones VALUES (152, 'detalle_solicitud_web_rectificar', '<table width="100%"  border="0" align="left" cellpadding="2" cellspacing="2">\r\n                 <tr >\r\n                   <td align="left" ><div align="left" style="border: 1px solid #999999; background-color: #D0E3FB;padding: 5px; width:600px"> D&iacute;as para rectificar: <strong>#DIAS_RECTIFICAR#</strong><br>\r\n                   </div></td>\r\n                   </tr>\r\n                 <tr >\r\n                   <td align="left" ><div align="left" style="border: 1px solid #999999; background-color: #f8f8f8;padding: 5px; width:600px"> N&ordm; de solicitud: <span class="style1"><strong>#ID_SOLICITUD#</strong></span>&nbsp;<br>\r\n                       Fecha ingreso: <span class="style1"><strong>#FECHA_INGRESO#</strong></span><span class="style1"><strong>\r\n                       <input type="hidden" name="folio" id="folio"  value="#ID_SOLICITUD# "/>\r\n                       </strong></span><br />\r\n                   </div></td>\r\n                   </tr>\r\n                 <tr >\r\n                   <td align="left" ><div align="left" style="border: 1px solid #999999; background-color: #f8f8f8;padding: 5px; width:600px"><table width="95%" border="0" align="left" cellpadding="2" cellspacing="0">\r\n               <tr>\r\n                 <td width="11%">Solicitante:</td>\r\n                 <td colspan="5"><span class="style1"><strong>#SOLICITANTE#</strong></span></td>\r\n               </tr>\r\n              #APODERADO#\r\n               <tr>\r\n                 <td>Direcci&oacute;n:</td>\r\n                 <td width="26%"><strong>#DIRECCION#</strong></td>\r\n                 <td width="11%">N&uacute;mero:</td>\r\n                 <td width="17%"><strong>#NUMERO#</strong></td>\r\n                 <td width="12%">Departamento:</td>\r\n                 <td width="23%"><strong>#DEPARTAMENTO#</strong></td>\r\n               </tr>\r\n               <tr>\r\n                 