INSERT INTO templates_acciones VALUES (153, 'detallle_solicitud_digitada', '\r\n<h3>Solicitud de Acceso a Informaci&oacute;n P&uacute;blica</h3>\r\n<br> <strong>#SERVICIO#</strong>\r\n              <p>Los campos indicados con (*) son obligatorios \r\n<fieldset><legend>Informaci&oacute;n del formulario</legend>\r\n  <table width="95%" border="0" cellpadding="0" cellspacing="0">\r\n    \r\n    <tr  style="border: 1px solid #999999; background-color: #D0E3FB;">\r\n      <td>D&iacute;as para rectificar</td>\r\n      <td  align="left"><strong>#DIAS_RECTIFICAR#</strong></td>\r\n    </tr>\r\n    <tr>\r\n      <td width="17%"><label>Folio</label></td>\r\n      <td width="83%"  align="left"><strong>#FOLIO#\r\n        <input type="hidden" name="folio" id="folio" value="#FOLIO#" />\r\n        <input type="hidden" name="id_solicitante" id="id_solicitante" value="#ID_SOLICITANTE#" />\r\n      </strong></td>\r\n    </tr>\r\n    <tr>\r\n      <td><label>Fecha</label></td>\r\n      <td><strong>#FECHA_INGRESO#</strong></td>\r\n    </tr>\r\n#DATOS_DERIVACION#\r\n  </table>\r\n\r\n  </fieldset>\r\n<fieldset><legend>Datos Personales</legend><br />\r\n<label>Tipo de Persona</label> <br />\r\n&nbsp; #TIPO_PERSONA# <br />\r\n<div class="showhide" id="cdiv0"><label>Nombres</label> *<br />\r\n \r\n  <input type="text" name="nombre" id="nombre" value="#NOMBRES#" />\r\n \r\n  (Francisco)<br />\r\n<label>Apellido Paterno</label> *<br />\r\n<input type="text" name="paterno" id="paterno" value="#PATERNO#" /> \r\n(Merino)<br />\r\n<label>Apellido Materno</label> *<br />\r\n<input type="text" name="materno" id="materno" value="#MATERNO#" /> \r\n(Echeverria)<br /><label>Apoderado</label></div>\r\n<div class="showhide" id="cdiv1"><label>Raz&oacute;n Social</label> *<br />\r\n  <input type="text" name="razon_social" id="razon_social" value="#RAZON_SOCIAL#"/>\r\n  <br />\r\n<label>Apoderado</label> *<br />\r\n</div>\r\n\r\n<input type="text" name="apoderado" id="apoderado" value="#APODERADO#" /><br />\r\n<label>Email</label> <br />\r\n<input type="text" name="email" id="email"  value="#EMAIL#" />\r\n</fieldset>\r\n             <br /><br />\r\n              <br />\r\n             <fieldset>\r\n             <legend> Domicilio</legend>\r\n             \r\n\r\n             <table class="tabla_nb" cellspacing="0" cellpadding="0" width="99%" align="left" border="0">\r\n  <tbody>\r\n    <tr>\r\n      <td align="left"><label>Direcci&oacute;n *</label>      </td>\r\n      <td colspan="3" align="left"><input type="text" name="direccion" id="direccion"  size="30" value="#DIRECCION#"/>\r\n        &nbsp;\r\n                <label>N&uacute;mero* </label>\r\n        <label>\r\n        <input type="text" name="numero" id="numero" size="3" value="#NUMERO#" />\r\n        &nbsp;&nbsp;&nbsp;Departamento \r\n        <input type="text" name="depto" id="depto" size="3" value="#DEPARTAMENTO#" />\r\n        </label></td>\r\n    </tr>\r\n    <tr>\r\n      <td align="left"><label>Ciudad *</label>      </td>\r\n      <td align="left"><input type="text" name="ciudad" id="ciudad" value="#CIUDAD#" /></td>\r\n      <td align="left">&nbsp;</td>\r\n      <td align="left">&nbsp;</td>\r\n    </tr>\r\n    <tr>\r\n      <td width="17%" align="left"><label>Regi&oacute;n  *</label>      </td>\r\n      <td width="34%" align="left">#REGION#</td>\r\n      <td width="17%" align="left"><label> Comuna *</label></td>\r\n      <td width="32%" align="left">#COMUNA#</td>\r\n    </tr>\r\n  </tbody>\r\n</table>\r\n<p><br />\r\n</p>\r\n             </fieldset>\r\n             <br />\r\n <br />\r\n     \r\n\r\n\r\n               <fieldset>\r\n              <legend>Informaci&oacute;n de la Solicitud</legend>\r\n              <label></label>\r\n              <label></label>\r\n               <br/>\r\n			  <label>Nombre de la entidad a la que dirige la solicitud &nbsp; </label>\r\n			   <strong>#ENTIDAD#</strong><br/>\r\n			   <br/>\r\n              <label>Identificaci&oacute;n de la informaci&oacute;n solicitada. Se&ntilde;ale la materia, fecha de \r\n			  emisi&oacute;n o per&iacute;odo de vigencia del documento, origen o destino, soporte, etc. *:</label>\r\n              <p>\r\n               <textarea name="identificacion_documentos" id="identificacion_documentos" cols="80" rows="8" class="textos">#IDENTIFICACION_DOCUMENTOS#</textarea>\r\n                <br />\r\n              </p>\r\n              <label></label>\r\n              <br />         \r\n            \r\n              </fieldset>\r\n               <br />\r\n              <br/>\r\n              <br/>\r\n<fieldset>\r\n              <legend>Documento Firmado</legend>\r\n              <br />\r\n<label>* &iquest;Este documento est&aacute; firmado? </label>\r\n#SI_FIRMADO#<label><br /><br />\r\n              </label>\r\n<label>*Si esta solicitud no est&aacute; firmada, volver&aacute; a ser ingresada al panel de solcitudes por rectificar.<br /></label>\r\n</fieldset>\r\n</fieldset> \r\n               <br /><br />\r\n              \r\n    <div align="center">\r\n                <input name="bt_rectificar" type="submit" class="botones" id="bt_rectificar" value="Rectificar" />\r\n</div>\r\n\r\n<script language="javascript">\r\n#JSCRIPT_COMBOS#\r\n</script>\r\n', 0);
