INSERT INTO templates_acciones VALUES (161, 'contenedor_rectificacion_solicitante', '<h3>Rectificar solicitud - Folio #FOLIO#</h3>\r\n<p>Esta  solicitud no cumple con la totalidad los requisitos m&iacute;nimos, favor le solicitamos se&ntilde;alar en el campo <strong>informaci&oacute;n de la solicitud</strong>, la identificaci&oacute;n clara de la informaci&oacute;n requerida. </p>\r\n\r\n<p> \r\n<div class="wide" id="table-block">\r\n<table cellspacing="0" cellpadding="0">\r\n    <tbody>\r\n  <tr class="header">\r\n          <td colspan="2">Motivo de la rectificaci&oacute;n</td>\r\n          <td>  </td>\r\n          <td>  </td>\r\n      </tr>\r\n        <tr>\r\n          <td colspan="4"><strong>#SUBESTADO#</strong></td>\r\n        </tr>\r\n   </tbody>\r\n  </table>\r\n</div>\r\n</p>\r\n<br />\r\n<div class="wide" id="table-block">\r\n  <table width="100%" cellpadding="0" cellspacing="0">\r\n    <tbody>\r\n        <tr class="header">\r\n          <td> Dirigida a:</td>\r\n          <td colspan="3"><strong>#SERVICIO#</strong></td>\r\n        </tr>\r\n        <tr >\r\n            <td width="23%">Entidad</td>\r\n            <td colspan="3"><strong>#ENTIDAD#</strong></td>\r\n        </tr>\r\n        <tr>\r\n            <td>Fecha de la solicitud</td>\r\n            <td width="30%"><strong>#FECHA_INGRESO#</strong></td>\r\n            <td width="10%">Estado</td>\r\n            <td width="37%"><strong>#ESTADO#\r\n              <input type="hidden" name="folio" id="folio" value="#FOLIO#" />\r\n            </strong></td>\r\n        </tr>\r\n        <tr>\r\n            <td>Fecha petici&oacute;n rectificaci&oacute;n</td>\r\n            <td><strong>#FECHA_PETICION_RECTIFICACION#</strong></td>\r\n          <td>Plazo para rectificar          &nbsp;</td>\r\n            <td><strong>#PLAZO_RECTIFICAR#</strong></td>\r\n        </tr>\r\n          <tr>\r\n            <td>Desea notificaci&oacute;n por correo electr&oacute;nico</td>\r\n            <td colspan="3"><strong>#MEDIO_NOTIFICACION#  &nbsp;  </strong></td>\r\n        </tr>\r\n       <tr>\r\n            <td>Forma de recepci&oacute;n</td>\r\n            <td colspan="3"><strong>#FORMA_RECEPCION# &nbsp;</strong></td>\r\n      </tr>\r\n     \r\n       <tr>\r\n            <td valign="top">Formato de entrega</td>\r\n            <td colspan="3" valign="top"><strong>#FORMA_ENTREGA# &nbsp;</strong><br><br></td>\r\n    </tr>\r\n\r\n      \r\n        <tr class="header">\r\n          <td colspan="4">Datos Personales - Los campos indicados con (*) son obligatorios</td>\r\n        </tr>\r\n       \r\n     \r\n    </tbody>\r\n</table>\r\n\r\n</div>\r\n\r\n<fieldset>\r\n<div class="showhide">\r\n <label>&nbsp;&nbsp;&nbsp;<strong>Tipo de Persona</strong></label>\r\n <br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;#TIPO_PERSONA#\r\n</div>\r\n<div class="showhide" id="cdiv0">\r\n\r\n\r\n  <table width="100%" border="0" cellpadding="0" cellspacing="0">\r\n    <tr>\r\n      <td><label><strong>Nombres</strong></label>*</td>\r\n      <td><label><strong>Apellido Paterno</strong></label>\r\n*</td>\r\n      <td><label><strong>Apellido Materno</strong></label>\r\n        *</td>\r\n      </tr>\r\n    <tr>\r\n      <td><input name="nombre" type="text" class="combo" id="nombre" value="#NOMBRES#" size="30" /></td>\r\n      <td><input name="paterno" type="text" class="combo" id="paterno" value="#PATERNO#" size="30" /></td>\r\n      <td><input name="materno" type="text" class="combo" id="materno" value="#MATERNO#" size="30" /></td>\r\n      </tr>\r\n  </table>\r\n</div>\r\n\r\n<div class="showhide" id="cdiv1">\r\n  <table width="100%" border="0" cellpadding="0" cellspacing="0">\r\n    <tr>\r\n      <td><label><strong>Raz&oacute;n Social</strong></label>\r\n*</td>\r\n      <td>&nbsp;</td>\r\n      <td>&nbsp;</td>\r\n    </tr>\r\n    <tr>\r\n      <td><input type="text" class="combo" name="razon_social" id="razon_social" value="#RAZON_SOCIAL#"/></td>\r\n      <td>&nbsp;</td>\r\n      <td>&nbsp;</td>\r\n    </tr>\r\n  </table>\r\n\r\n</di